mapper.update:
update cl_article_type set
version = version + 1

    ,type_name = #{typeName}


    ,type_count = #{typeCount}

where type_id = #{typeId}
and version = #{version}

update cl_article_type set
is_deleted = 1
where type_id = #{id}

update cl_article_type set
version = version + 1,
is_enabled = #{isEnabled}
where type_id = #{typeId}
and version = #{version}
